import React from "react";  
import { useState } from "react";
import { print } from "./print";  
import StackedBarChart from "./StackedBarChart"

export const Barchart = () => {
    const input = require("./data.json")

    let year = new Set()
    Object.values(input).forEach((v) => {allKeys.add(v.year)})    
    year = Array.from(year)

    let allKeys = new Set()
    Object.values(input).forEach((v) => {allKeys.add(v.level_2)})      
    allKeys = Array.from(allKeys)

    let res = input.reduce((acc, {year, level_2, value}) =>
    {
        acc[year] = acc[year] || new Set();
        acc[year][level_2] = parseInt(value)
        acc[year]["year"] = parseInt(year)
        return acc;
    }, {})

    let data = []

    year.forEach(k => {
         data.push(res[k])
    });


    const getRandomColor = () => {
        let letters = '0123456789ABCDEF';
        let color = '#';
        for (let i = 0; i < 6; i++) {
          color += letters[Math.floor(Math.random() * 16)];
        }
        return color;
    }

    let colors = {
        '2011': "#656431",
        '2012': "#68A696",
        '2013': "#9F2964",
        '2014': "#C19131",
        '2015': "#A5235F",
        '2016': "#67ED30",
        '2017': "#14C069",
        '2018': "#109AE0",
        '2019': "#DCB545",
        '2020': "#BF354F"
    }

    print(data)

    const [keys, setKeys] = useState(allKeys)


    return (
        <React.Fragment>
            <h2>Stacked Bar Chart with D3 </h2>
            <StackedBarChart data={data} keys={keys} colors={colors} />

            <div className="fields">
                {allKeys.map(key => (
                <div key={key} className="field">
                    <input
                    id={key}
                    type="checkbox"
                    checked={keys.includes(key)}
                    onChange={e => {
                        if (e.target.checked) {
                        setKeys(Array.from(new Set([...keys, key])));
                        } else {
                        setKeys(keys.filter(_key => _key !== key));
                        }
                    }}
                    />
                    <label htmlFor={key} style={{ color: colors[key] }}>
                    {key}
                    </label>
                </div>
                ))}
            </div>
        </React.Fragment>
    )
}
