import React from "react";  
import { useEffect, useState } from "react";
import { print } from "./print";  
import StackedBarChart from "./StackedBarChart"

export const Barchart = () => {
    const data = require("./data.json")

    let keys = new Set()
    Object.values(data).forEach((v) => {keys.add(v.year)})      
    keys = Array.from(keys)

    let res = data.reduce((acc, {year, level_2, value}) =>
    {
        acc[year] = acc[year] || new Set();
        acc[year][level_2] = value
        acc[year][year] = year
        return acc;
    }, {})

    let values = []

    keys.forEach(k => {
         values.push(res[k])
    });

    let colors = {}

    const getRandomColor = () => {
        let letters = '0123456789ABCDEF';
        let color = '#';
        for (let i = 0; i < 6; i++) {
          color += letters[Math.floor(Math.random() * 16)];
        }
        return color;
    }

    keys.forEach(k => {
        colors[k] = getRandomColor() 
    })

    print(colors)

    const [currentKeys, setCurrentKeys] = useState(keys)


    return (
        <React.Fragment>
            <StackedBarChart data={values} keys={keys} colors={colors} />

            <div className="fields">
                {keys.map(key => (
                    <div key={key} className="field">
                        <input id={key} 
                        type="checkbox" 
                        checked={currentKeys.includes(key)} 
                        onChange={e => { 
                            if (e.target.checked) {
                                setCurrentKeys(Array.from(new Set([...currentKeys, key])))
                            } else {
                                setCurrentKeys(currentKeys.filter(_key => _key !== key))
                        }}} 
                        />
                        <label for={key} style={{color:colors[key]}}>key</label>
                    </div>
                ))}
            </div>

        </React.Fragment>
    )
}
