import React from "react";  
import { useState } from "react";
import { print } from "./print";  
import StackedBarChart from "./StackedBarChart"

export const Barchart = () => {
    const data = require("./data.json")

    let allKeys = new Set()
    Object.values(data).forEach((v) => {allKeys.add(v.year)})      
    allKeys = Array.from(allKeys)

    let res = data.reduce((acc, {year, level_2, value}) =>
    {
        acc[year] = acc[year] || new Set();
        acc[year][level_2] = value
        acc[year]["year"] = year
        return acc;
    }, {})

    let values = []

    allKeys.forEach(k => {
         values.push(res[k])
    });

    let colors = {}

    const getRandomColor = () => {
        let letters = '0123456789ABCDEF';
        let color = '#';
        for (let i = 0; i < 6; i++) {
          color += letters[Math.floor(Math.random() * 16)];
        }
        return color;
    }

    allKeys.forEach(k => {
        colors[k] = getRandomColor()
    })

    print(colors)

    const [keys, setKeys] = useState(allKeys)


    return (
        <React.Fragment>
            <h2>Stacked Bar Chart with D3 </h2>
            <StackedBarChart data={values} keys={keys} colors={colors} />

            <div className="fields">
                {allKeys.map(key => (
                <div key={key} className="field">
                    <input
                    id={key}
                    type="checkbox"
                    checked={keys.includes(key)}
                    onChange={e => {
                        if (e.target.checked) {
                        setKeys(Array.from(new Set([...keys, key])));
                        } else {
                        setKeys(keys.filter(_key => _key !== key));
                        }
                    }}
                    />
                    <label htmlFor={key} style={{ color: colors[key] }}>
                    {key}
                    </label>
                </div>
                ))}
            </div>
        </React.Fragment>
    )
}
